INSERT INTO brand (id, long_name, short_name, description, note, img_src_path) VALUES ('coke', 'Coca Cola', 'Coke', 'It was 1886, John Pemberton, an Atlanta pharmacist, was inspired by simple curiosity. One afternoon, he stirred up a fragrant, caramel-coloured liquid and, when it was done, the mixture was combined with carbonated water and sampled by customers who all agreed - this new drink was something special!', 'New York Harbour, 1886', 'assets/images/coca_cola.jpg');
INSERT INTO brand (id, long_name, short_name, description, note, img_src_path) VALUES ('sprite', 'Sprite', 'Sprite', 'First introduced in 1961, crisp, refreshing, clean-tasting Sprite is now the world''s leading lemon and lime flavoured soft drink and is sold in more than 190 different countries. Sprite Zero, part of our no sugar Zero range, offers the delicious lemon lime taste of Sprite without the sugar or calories.', 'West Germany, 1959', 'assets/images/sprite.jpg');
INSERT INTO brand (id, long_name, short_name, description, note, img_src_path) VALUES ('pepper', 'Dr. Pepper', 'Pepper', 'Dr Pepper''s unique, sparkline blend of 23 fruit flavours has been around for well over a century and it''s still the same, with that distinctive flavour you just can''t quite put your tongue on. It was created by Texas pharmacist Charles Alderton in 1885. The distinctive, bold taste of Dr Pepper has been popular ever since.', 'Texas, 1885', 'assets/images/dr_pepper.jpg');
INSERT INTO brand (id, long_name, short_name, description, note, img_src_path) VALUES ('costa', 'Costa Coffee', 'Costa', 'The Costa Coffee story started over 45 years ago with two brothers and just one dream. Since then, a lot has changed, but our values and commitment to serving great tasting coffee hasn''t - it''s in our DNA. Now, what started as the UK''s favorite coffee shop is now available all around the world, bringing the care and quality of a local caf√© to coffee lovers globally.', 'London, 1971', 'assets/images/costa.jpg');
INSERT INTO brand (id, long_name, short_name, description, note, img_src_path) VALUES ('fanta', 'Fanta', 'Fanta', 'Bright, bubbly and popular, Fanta is the soft drink that intensifies fun. Introduced in 1940, Fanta is the second oldest brand of The Coca-Cola Company. Bright, bubbly and a popular favorite, Fanta Orange is a soft drink with a tingly, fruity taste. ', 'Germany, 1940', 'assets/images/fanta.jpg');
INSERT INTO brand (id, long_name, short_name, description, note, img_src_path) VALUES ('appletizer', 'Appletizer', 'Appletizer', 'Appletiser is a non-alcoholic sparkling juice that contains no added sugar or preservatives. It''s an everyday indulgent treat for adults to enjoy in special social occasions.  It was created in 1966 in Elgin Valley, Western Cape, South Africa, by French-Italian immigrant Edmond Lombardi. For export and out of season, the juice is concentrated by extracting the aroma and the water from it.', 'South Africa, 1966', 'assets/images/appletizer.jpg');