INSERT INTO brand (id, long_name, short_name, description, note, img_src_path) VALUES ('coke', 'Coca Cola', 'Coke', 'It was 1886, John Pemberton, an Atlanta pharmacist, was inspired by simple curiosity. One afternoon, he stirred up a fragrant, caramel-coloured liquid and, when it was done, the mixture was combined with carbonated water and sampled by customers who all agreed - this new drink was something special!', 'New York Harbour, 1886', '/assets/images/coca_cola.jpg');
INSERT INTO brand (id, long_name, short_name, description, note, img_src_path) VALUES ('sprite', 'Sprite', 'Sprite', 'First introduced in 1961, crisp, refreshing, clean-tasting Sprite is now the world''s leading lemon and lime eflavoured soft drink and is sold in more than 190 different countries. Sprite Zero, part of our no sugar Zero range, offers the delicious lemon lime taste of Sprite without the sugar or calories.', 'West Germany, 1959', '/assets/images/sprite.jpg');
INSERT INTO brand (id, long_name, short_name, description, note, img_src_path) VALUES ('pepper', 'Dr. Pepper', 'Pepper', 'Dr Pepper''s unique, sparkline blend of 23 fruit flavours has been around for well over a century and it''s still the same, with that distinctive flavour you just can''t quite put your tongue on. It was created by Texas pharmacist Charles Alderton in 1885. The distinctive, bold taste of Dr Pepper has been popular ever since.', 'Texas, 1885', '/assets/images/pepper.jpg');